mapper.select:
SELECT count(permission.id) as listCount
FROM permission, permissiondetail, permissionuser
WHERE permission.id = permissiondetail.permission_id
  AND permission.id = permissionuser.permission_id
  AND permissiondetail.target = #{target}
  AND permissiondetail.func = #{func}
  AND permissionuser.user_id = #{userId}
  AND permission.deleted = false
  AND permissiondetail.deleted = false
  AND permissionuser.deleted = false

mapper.sql:
WHERE permission.id is not null
AND permission.deleted = #{deleted}
AND permission.title LIKE CONCAT('%', #{title}, '%')

mapper.select:
SELECT permission.id FROM permission
AND #{cursor} > permission.id AND permission.id > #{cursor}
ORDER BY permission.id DESC ASC Limit 0, #{perpage}
